import React from "react";

class Image extends React.Component {
  constructor(props) {
    super(props);
    this.path = "/" + props.path;
  }
  render() {
    return (
      <div>
        <img src={this.path} alt="画像" />
      </div>
    );
  }
}

export default Image;
