import React from "react";
import Head from "next/head";
import Style from "../style/style";
import footer_style from "../style/footer_style";
import Footer from "../components/Footer";

class App extends React.Component {
  render() {
    return (
      <div>
        <html lang="ja" />
        <Head>
          <title>React</title>
          <meta charSet="utf-8" />
          <meta
            name="viewport"
            content="initial-scale=1.0,width=divice-width"
          />
        </Head>
        {Style}
        <h2>#目次</h2>
        <h3>1.Reactとは？</h3>
        <h3>2.JSX</h3>
        <h3>3.Hello React</h3>
        <h3>4.state</h3>
        <h3>5.props</h3>
        <h3>6.合わせて覚えたいもの</h3>
        <h3>7.最後に</h3>
        {footer_style}
        <Footer go="./Page2" />
      </div>
    );
  }
}

export default App;
