const Style = (
  <style>
    {`

        body,h1,h2,h3,div,p {
            margin: 0;
            padding: 0;
        }

        body {
           background-color: gray;
        }

        .arrow{
            position: relative;
            display: inline-block;
            padding: 0 0 0 16px;
            color: #000;
            vertical-align: middle;
            text-decoration: none;
            font-size: 15px;
        }
        .arrow::before,
        .arrow::after{
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            margin: auto;
            content: "";
            vertical-align: middle;
        }
    `}
  </style>
);

export default Style;
