import React from "react";
import Subject from "../components/Subject";
import Footer from "../components/Footer";
import Header from "../components/Header";

class Page16 extends React.Component {
  render() {
    return (
      <div>
        <Header />
        <div className="backimg">
          <div className="transparent">
            <div className="wrap">
              <Subject subject="#propsとは" />
              <div className="char">
                <p>component 生成時に親から渡されるオブジェクト</p>
                <p>
                  component が画面から排除されるまで<span>不変</span>の値
                </p>
                <p>
                  <span>Immutable</span> であり外部とのインターフェースの役割
                </p>
                <p>
                  propsは <span>「props名=値」</span>
                  という形で コンポーネントを呼び出す箇所で渡す
                </p>
                <p>同じcomponentでも渡す値によって表示が変わる</p>
              </div>
            </div>
          </div>
          <Footer back="./Page15" go="./Page17" />
        </div>
      </div>
    );
  }
}

export default Page16;
