import React from "react";
import Subject from "../components/Subject";
import Footer from "../components/Footer";
import Header from "../components/Header";

class Page8 extends React.Component {
  render() {
    return (
      <div>
        <Header />
        <div className="backimg">
          <div className="transparent">
            <div className="wrap">
              <Subject subject="#JSXの注意点" />
              <div className="char">
                <p>JSXはほとんどHTMLと同じだが いくつか違いがある</p>
                <p>左の図のように return内に複数の要素があるとエラー</p>
                <p>
                  複数の要素がある場合は 右の図のように div タグで囲んで
                  1つの要素にまとめてあげる
                </p>
              </div>
              <div className="color_char">
                <p>HTMLと似ているので</p>
                <p>慣れたらデザイナーでも分かりやすい！</p>
              </div>
            </div>
            <Footer back="./Page6" go="./Page8" />
          </div>
        </div>
      </div>
    );
  }
}

export default Page8;
