import React from "react";
import Head from "next/head";
import Style from "../style/style";
import Subject from "../components/Subject";
import Footer from "../components/Footer";

class Page3 extends React.Component {
  render() {
    return (
      <div>
        <html lang="ja" />
        <Head>
          <title>React</title>
          <meta charSet="utf-8" />
          <meta
            name="viewport"
            content="initial-scale=1.0,width=divice-width"
          />
        </Head>
        {Style}
        <div className="backimg">
          <div className="transparent">
            <div className="wrap">
              <Subject subject="#Reactとは？" />
              <p>Facebook製のオープンソース</p>
              <p>
                SPA(Single-Page Application) を実現する JavaScript
                フレームワーク
              </p>
              <p>
                JavaScript の中に直接 HTML/XML を記述する JSX という技術を利用
              </p>
              <p>
                JavaScript は ES6 の文法である import
                やアロー関数を取り入れている
              </p>
            </div>
            <Footer back="./Page2" go="./Page4" />
          </div>
        </div>
      </div>
    );
  }
}

export default Page3;
