import React from "react";
import Subject from "../components/Subject";
import Footer from "../components/Footer";
import Header from "../components/Header";

class Page22 extends React.Component {
  render() {
    return (
      <div>
        <Header />
        <div className="backimg">
          <div className="transparent">
            <div className="wrap">
              <Subject subject="#stateの表示" />
              <div className="char">
                <p>
                  定義したstateは <span>this.state</span>で取得することができる
                </p>
                <p>
                  this.stateはオブジェクトなので
                  <span>this.state.プロパティ名</span>
                  とすることで
                  指定したstateのプロパティ名に対応する値を取得できる
                </p>
              </div>
            </div>
          </div>
          <Footer back="./Page21" go="./Page23" />
        </div>
      </div>
    );
  }
}

export default Page22;
